where $x/residence = "No"

return

  {$x/confirmed_cases}
  {$x/confirmed_cases_rate}



  {$t/confirmed_cases}
  {$t/confirmed_cases_rate}



  {$y/confirmed_cases}
  {$y/confirmed_cases_rate}



  {$z/confirmed_cases}
  {$z/confirmed_cases_rate}

 }
